//Dependencies
import React,{Component} from 'react';
import {Button, ButtonToolbar, Card, Col, Form, Row, Container} from "react-bootstrap";

class LoginPage extends Component{
  constructor(props){
    super(props);
    this.state = {
      validated: false,
    }
  }

  handleSubmit(event) {
  const form = event.currentTarget;
  if (form.checkValidity() === false) {
    event.preventDefault();
    event.stopPropagation();
  }
  this.setState({ validated: true });
}

  render(){
    const {validated} = this.state;
    return(
      <Container style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '80vh'}}>

          <Row className="justify-content-md-center">
            <Col md="auto">

            <Card className="text-center"  >
              <Card.Header>
                  <h1>Start shopping @ VinCat </h1>
                  <ButtonToolbar  className="justify-content-md-center">
                  <Button variant="outline-dark">Log in with Google</Button>
                  </ButtonToolbar>
              </Card.Header>

              <Card.Body >
                  <Form
                  className="justify-content-md-center"
                  noValidate
                  validated={validated}
                  onSubmit={e => this.handleSubmit(e)}
                  >
                      <Form.Group as={Row} controlId="formHorizontalEmail" className="justify-content-md-center">
                          <Col sm={5}>
                              <Form.Control
                              required
                              type="email"
                              placeholder="Email"
                              />
                              <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                              <Form.Control.Feedback type="invalid">
                                Choose a valid email!
                              </Form.Control.Feedback>
                          </Col>
                      </Form.Group>

                      <Form.Group as={Row}
                      controlId="formHorizontalPassword"
                      className="justify-content-md-center"
                      onChange={this.handleChange}
                      >

                          <Col sm={5}>
                              <Form.Control required type="password" placeholder="Password" />
                              <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                          </Col>
                      </Form.Group>
                      <Form.Group as={Row} className="justify-content-md-center">
                          <Col sm={5}>

                              <Card.Link href="/forgot">Forgot Password?</Card.Link>


                          </Col>
                      </Form.Group>
                      <Form.Group as={Row} className="justify-content-md-center">

                          <Col sm={8}>

                              <Card.Link href="/register">Don't have and account? Register</Card.Link>


                          </Col>
                      </Form.Group>
                      <Form.Group as={Row} controlId="formHorizontalCheck" className="justify-content-md-center">
                          <Col sm={5}>
                              <Form.Check label="Remember me" />
                          </Col>
                      </Form.Group>

                      <Form.Group as={Row} className="justify-content-md-center">
                          <Col sm={5}>
                              <Button type="submit" onClick={this.handleClick}>Login</Button>
                          </Col>
                      </Form.Group>


                  </Form>
              </Card.Body>

          </Card>

              </Col>
          </Row>
      </Container>
    );
  }
}

export default LoginPage;
