//Dependencies
import React,{Component} from 'react';
import {Button, ButtonToolbar, Card, Col, Form, Row, Container} from "react-bootstrap";

class LoginPage extends Component{
  constructor(props){
    super(props);
    this.state = {
      email: "",
      password: "",
      validated: false,
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange(e){
    (e.target.id==="formHorizontalEmail")?this.setState({email:e.target.value}):this.setState({password:e.target.value});
    console.log(this.state);
  }

  handleClick(e){
    console.log(this.state);
    if(this.state.email === "a@a" && this.state.password != ""){
      this.setState({
        validated:true,
      })
      console.log("succes");
    }else{
      this.setState({
        validated:false,
      })
      console.log("error");
    }
  }
  render(){
    const validated = this.state.validated;
    return(
      <Container style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '80vh'}}>

          <Row className="justify-content-md-center">
            <Col md="auto">

          <Card className="text-center">
              {/*Login component header*/}
              <Card.Header>
                  <h1>Start shopping @ VinCat </h1>
                  <ButtonToolbar  className="justify-content-md-center">
                  <Button variant="outline-dark">Log in with Google</Button>
                  </ButtonToolbar>
              </Card.Header>

              {/*Login component body*/}
              <Card.Body >
                <Form className="justify-content-md-center" onSubmit={this.handleClick} validated={validated}>
                {/*email field*/}
                  <Form.Group as={Row}
                  controlId="formHorizontalEmail"
                  className="justify-content-md-center"
                  >
                      <Col sm={5}>
                        <Form.Control type="email" placeholder="Email" onInput={this.handleChange} onSubmit={console.log("submit")}/>
                      </Col>
                    </Form.Group>
                    {/*password field*/}
                    <Form.Group as={Row}
                    controlId="formHorizontalPassword"
                    className="justify-content-md-center"
                    >
                      <Col sm={5}>
                        <Form.Control type="password" placeholder="Password" onInput={this.handleChange}/>
                      </Col>
                    </Form.Group>
                    {/*Forgot Password link*/}
                    <Form.Group as={Row}
                    className="justify-content-md-center"
                    >
                      <Col sm={5}>
                        <Card.Link href="/forgot">Forgot Password?</Card.Link>
                      </Col>
                    </Form.Group>
                      <Form.Group as={Row} className="justify-content-md-center">

                          <Col sm={8}>

                              <Card.Link href="/register">Don't have and account? Register</Card.Link>


                          </Col>
                      </Form.Group>
                      <Form.Group as={Row} controlId="formHorizontalCheck" className="justify-content-md-center">
                          <Col sm={5}>
                              <Form.Check label="Remember me" />
                          </Col>
                      </Form.Group>

                      <Form.Group as={Row} className="justify-content-md-center">
                          <Col sm={5}>
                              <Button type="submit">Login</Button>
                          </Col>
                      </Form.Group>
                  </Form>
              </Card.Body>

          </Card>

              </Col>
          </Row>
      </Container>
    );
  }
}

export default LoginPage;
