//Dependencies
import React,{Component} from 'react';
import {Button, ButtonToolbar, Card, Col, Form, Row, Container} from "react-bootstrap";

const emailRegex =/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/ ;

class LoginPage extends Component{
  constructor(props){
    super(props);
    this.state = {
      email: "",
      password: "",
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleValidation = this.handleValidation.bind(this);
  }

  handleChange(e){
    this.handleValidation();
    (e.target.id==="formHorizontalEmail")?this.setState({email:e.target.value}):this.setState({password:e.target.value});
    console.log(this.state);
  }

  handleClick(e){
    console.log(this.state);
  }

  handleValidation(){
    if(this.state.email.match(emailRegex) && this.state.email != "" && this.state.password != ""){
      this.setState({
        validated:true,
      })
    }else{
      this.setState({
        validated:false,
      })
    }

  }

  render(){
    const validated = this.state.validated;
    return(
      <Container style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '80vh'}}>

          <Row className="justify-content-md-center">
            <Col md="auto">

          <Card className="text-center">
              {/*Login component header*/}
              <Card.Header>
                  <h1>Start shopping @ VinCat </h1>
                  <ButtonToolbar  className="justify-content-md-center">
                  <Button variant="outline-dark">Log in with Google</Button>
                  </ButtonToolbar>
              </Card.Header>

              {/*Login component body*/}
              <Card.Body >
                <Form noValidate className="justify-content-md-center" validated={validated}>
                {/*email field*/}
                  <Form.Group as={Row}
                  controlId="formHorizontalEmail"
                  className="justify-content-md-center"
                  >
                    <Col sm={5}>
                      <Form.Control type="email" placeholder="Email" onChange={this.handleChange}/>
                    </Col>
                  </Form.Group>
                  {/*password field*/}
                  <Form.Group as={Row}
                  controlId="formHorizontalPassword"
                  className="justify-content-md-center"
                  >
                    <Col sm={5}>
                      <Form.Control type="password" placeholder="Password" onChange={this.handleChange}/>
                    </Col>
                  </Form.Group>
                  {/*Forgot Password link*/}
                  <Form.Group as={Row}
                    className="justify-content-md-center"
                  >
                    <Col sm={5}>
                      <Card.Link href="/forgot">Forgot Password?</Card.Link>
                    </Col>
                  </Form.Group>
                  {/*Register link*/}
                  <Form.Group as={Row}
                  className="justify-content-md-center"
                  >
                    <Col sm={8}>
                      <Card.Link href="/register">Don't have and account? Register</Card.Link>
                    </Col>
                  </Form.Group>
                      <Form.Group as={Row} controlId="formHorizontalCheck" className="justify-content-md-center">
                          <Col sm={5}>
                              <Form.Check label="Remember me" />
                          </Col>
                      </Form.Group>

                      <Form.Group as={Row} className="justify-content-md-center">
                          <Col sm={5}>
                              <Button type="button" onClick={this.handleClick}>Login</Button>
                          </Col>
                      </Form.Group>
                  </Form>
              </Card.Body>

          </Card>

              </Col>
          </Row>
      </Container>
    );
  }
}

export default LoginPage;
